# !/usr/bin/env python# -*- coding: utf-8 -*-import xadminfrom .models import Articlefrom xadmin import viewsclass ArticleAdmin(object):    style_fields = {"detail": "ueditor"}class BaseSetting(object):    enable_themes = True    use_bootswatch = Trueclass GlobalSettings(object):    site_title = "后台管理系统"    site_footer = "DEMO"    menu_style = "accordion"xadmin.site.register(Article, ArticleAdmin)xadmin.site.register(views.BaseAdminView, BaseSetting)xadmin.site.register(views.CommAdminView, GlobalSettings)